apiVersion: n9/v1alpha
kind: SLO
metadata:
  displayName: $1
  name: $1
  project: $2
spec:
  alertPolicies:
  - fast-burn-5x-for-last-10m
  anomalyConfig:
    noData:
      alertMethods:
      - name: slack-notification
        project: default
  attachments:
  - displayName: Nobl9 Documentation
    url: https://docs.nobl9.com
  budgetingMethod: Occurrences
  description: $3
  indicator:
    metricSource:
      kind: Agent
      name: elasticsearch
      project: default
  objectives:
  - countMetrics:
      good:
        elasticsearch:
          index: apm-7.13.3-transaction
          query: "{
  "query": {
    "bool": {
      "must": [
        {
          "match": {
            "service.name": "api-server"
          }
        }
      ],
      "filter": [
        {
          "range": {
            "@timestamp": {
              "gte": "{{.BeginTime}}",
              "lte": "{{.EndTime}}"
            }
          }
        },
        {
          "match": {
            "transaction.result": "HTTP 2xx"
          }
        }
      ]
    }
  },
  "size": 0,
  "aggs": {
    "resolution": {
      "date_histogram": {
        "field": "@timestamp",
        "fixed_interval": "{{.Resolution}}",
        "min_doc_count": 0,
        "extended_bounds": {
          "min": "{{.BeginTime}}",
          "max": "{{.EndTime}}"
          "
        }
      },
      "aggs": {
        "n9-val": {
          "value_count": {
            "field": "transaction.result"
          }
        }
      }
    }
  }
}"
